INSERT INTO wocka VALUES ('Did you hear the one about the Polish wolf?<br /><br><br /><br>He chewed off three legs and was still caught in the trap.','Animal',44,'Polish Wolf');
INSERT INTO wocka VALUES ('A polar bear walked into a bar and said<br /><br>"Can I please have a gin and............................................tonic?"<br /><br>The bartender replied "Sure, but why the large pause?"<br /><br>"I don''t know, I''ve always had them!"','Puns',45,'Polar Bear');
