INSERT INTO wocka VALUES ('Police officer: "Excuse me, but your dog has been chasing a man on his bicycle".<br /><br>Dog owner: "Are you crazy? My dog can''t even ride a bicycle".','Animal',1702,'The Dog');
INSERT INTO wocka VALUES ('Attending the funeral of an actress who had been married ten times, a friend sobbed to the priest, "Well, at least they''re together at last."<br /><br>The clergyman looked around. "Which of her husbands is buried here?"<br /><br>"None," said the friend. "I meant, her legs."','Men / Women',1703,'Old Tart');
